sources.base.css:
*,
*::after,
*::before {
  box-sizing: border-box;
}

:root {
  font-size: 16px;
}

/*font-family: 'Merriweather', serif;*/

body {
  margin: 0;
  --color-text: #fff;
  --color-bg: #111;
  --color-link: #aaa;
  --color-link-hover: #fff;
  --color-alt: #7f6a57;
  color: var(--color-text);
  background-color: var(--color-bg);
  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  counter-reset: contentSection;
  font-family: Helvetica, sans-serif;
}

a {
  text-decoration: underline;
  color: var(--color-link);
  outline: none;
}

a:hover,
a:focus,
a:active {
  color: var(--color-link-hover);
  outline: none;
  text-decoration: none;
}

main {
  overflow: hidden;
  position: relative;
}

.menu__links {
  display: flex;
  justify-content: flex-end;
  padding-top: 2rem;
  padding-bottom: 2rem;
  padding-right: 3rem;
  z-index: 1000;
}

.menu__link {
  text-decoration: none;
  color: var(--color-link);
  cursor: pointer;
  outline: none;
  margin-left: 2rem;
  z-index: 1000;
}

.content {
  padding: 3rem;
  min-height: 80vh;
  display: flex;
  position: relative;
  flex-direction: column;
  align-content: center;
  justify-content: space-between;
}

.content--feature {
  justify-content: flex-start;
  min-height: 0;
}

.content__breakout {
  margin: 0 -3rem;
  white-space: nowrap;
  text-transform: uppercase;
  font-family: "Merriweather", serif;
  font-weight: 300;
  font-style: italic;
  color: #211f1b;
}

.content__breakout--big {
  font-size: 13vw;
}

.content__breakout--medium {
  font-size: 5vw;
}

.content--numbered::after {
  counter-increment: contentSection;
  content: counter(contentSection, decimal-leading-zero);
  position: absolute;
  top: 50%;
  right: 3rem;
  border: 1px solid;
  color: var(--color-alt);
  width: 4rem;
  height: 4rem;
  margin-top: -2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
}

.content__pretitle {
  font-size: 1.85rem;
  margin-top: auto;
  color: var(--color-alt);
}

.content__pretitle::before {
  content: "\2015";
  margin-right: 0.25rem;
}

.content__title {
  font-size: 9vw;
  font-weight: 400;
  line-height: 0.8;
  margin: 0 0 3rem 0;
  max-width: 800px;
  will-change: transform;
}

.content__text--right {
  align-self: flex-end;
}

.content__text--wide {
  max-width: 1000px;
  width: 65vw;
}

@media screen and (min-width: 53em) {
  .content__text {
    max-width: 800px;
    width: 50vw;
    min-width: calc(300px - 6rem);
  }
}

.content__text {
  font-size: clamp(1rem, 4vw, 2.25rem);
}

.content__text--centered {
  margin: auto;
}

/* Tiles Grid */
.tiles {
  --tiles-height: 52vw;
  height: var(--tiles-height);
  position: relative;
  overflow: hidden;
}

.tiles--columns-rotated {
  --tiles-height: 180vmax;
}

.tiles--darker .tiles__line-img {
  opacity: 0.7;
}

.tiles--perspective {
  overflow: visible;
  perspective: 1000px;
  --tiles-height: 500px;
}

.tiles--perspective .tiles__wrap {
  width: 200%;
  transform: translate3d(-50%, -50%, 0) translateX(-25%) translateZ(-1200px)
    rotateX(75.5deg) rotateZ(12deg);
}

.tiles__wrap {
  width: 150%;
  --tileswrap-height: var(--tiles-height);
  height: var(--tileswrap-height);
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate3d(-50%, -50%, 0);
}

.tiles--rotated .tiles__wrap {
  width: 150%;
  position: absolute;
  left: 50%;
  top: 0%;
  --tileswrap-height: calc(var(--tiles-height) * 1.2);
  transform: translate3d(-50%, -50%, 0) rotate(22.5deg);
}

.tiles--columns .tiles__wrap {
  width: 100%;
  display: flex;
  --tileswrap-height: calc(var(--tiles-height) * 1.4);
  transform: translate3d(0, -50%, 0);
  left: 0;
  top: 50%;
}

.tiles--columns-rotated .tiles__wrap {
  width: 150%;
  display: flex;
  transform: translate3d(-50%, -50%, 0) rotate(-22.5deg);
  left: 50%;
  top: 50%;
  align-items: center;
  justify-content: center;
}

.tiles--columns-rotated .tiles__line {
  width: 320px;
  flex: none;
  padding: 0 1.5vw;
  display: block;
}

.tiles--columns-rotated .tiles__line:nth-child(even) {
  margin-top: -160px;
}

.tiles--columns-rotated .tiles__line-img {
  --tile-margin: 3vw 0;
  width: 100%;
  height: 400px;
}

.tiles--columns-rotated .tiles__line {
  width: 33vmax;
}

.tiles--columns-rotated .tiles__line {
  width: 33vmax;
}
.tiles--columns-rotated .tiles__line:nth-child(even) {
  margin-top: -20vmax;
}
.tiles--columns-rotated .tiles__line-img {
  height: 40vmax;
}

.tiles--small {
  --tiles-height: 240px;
  margin: 10vh 0 0;
}

.tiles__line {
  display: flex;
}

.tiles--columns .tiles__line {
  width: 25%;
  padding: 0 1rem;
  display: block;
}

.tiles--small .tiles__line {
  height: 100%;
}

.tiles__line-img {
  --tile-margin: 2vw;
  flex: none;
  width: calc(16.6666% - var(--tile-margin) * 2);
  height: calc(var(--tileswrap-height) / 3 - (3 * var(--tile-margin) / 2));
  margin: var(--tile-margin);
  background-size: cover;
  background-position: 50% 50%;
}

.tiles--columns .tiles__line-img {
  --tile-margin: 2rem 0;
  width: 100%;
  height: calc(25vw * 1.1);
}

.tiles--small .tiles__line-img {
  --tile-margin: 5px;
  margin: 0 var(--tile-margin);
  width: calc(12.5% - var(--tile-margin) * 2);
  height: 100%;
}

.tiles--perspective .tiles__line-img {
  backface-visibility: hidden;
  outline: 1px solid transparent;
  --tile-margin: 1vw;
  width: calc(16.6666% - var(--tile-margin) * 2);
  height: calc(16.666vw * 1.3);
}

.tiles--oneline {
  --tiles-height: 50vh;
  min-height: 400px;
  margin: 10vh 0;
}

.tiles--oneline .tiles__line {
  height: 100%;
}

.tiles--oneline .tiles__line-img {
  --tile-margin: 1vw;
  margin: 0 var(--tile-margin);
  width: calc(25% - var(--tile-margin) * 2);
  height: 100%;
}

.tiles--fixed {
  --tiles-height: 65vw;
  margin-top: 25vh;
}

.tiles--fixed .tiles__wrap {
  width: 100%;
}

.tiles--fixed .tiles__line-img {
  --tile-margin: 10px;
  -webkit-filter: brightness(0.8);
  width: calc(16.6666% - var(--tile-margin) * 2);
  height: calc(var(--tileswrap-height) / 3 - (3 * var(--tile-margin) / 2));
}

.tiles__title {
  position: absolute;
  height: 100vh;
  width: 100%;
  display: grid;
  place-items: center;
  font-size: 10vw;
  padding: 3rem;
  margin: 0;
  line-height: 0.8;
  font-family: span, serif;
  font-weight: 700;
  font-style: normal;
}

.tiles__title--intro {
  display: flex;
  justify-content: flex-start;
  align-items: center;
}

.tiles__title--alt {
  font-size: 9vw;
  font-size: clamp(2rem, 9vw, 7.25rem);
}

.tiles__title--full {
  height: 100%;
}

.tiles__title--right {
  justify-content: flex-end;
}

.tiles__title--left {
  justify-content: flex-start;
}

.backtop {
  margin: auto auto;
  font-size: 4rem;
  cursor: pointer;
  text-decoration: none;
  transition: 200ms ease;
}

.backtop::after {
  content: "\2934";
  font-size: 3vw;
  vertical-align: top;
  margin-left: 1rem;
}

/*! locomotive-scroll v3.5.4 | MIT License | https://github.com/locomotivemtl/locomotive-scroll */
html.has-scroll-smooth {
  overflow: hidden;
}

html.has-scroll-dragging {
  -webkit-user-select: none;
  -moz-user-select: none;
  -ms-user-select: none;
  user-select: none;
}

.has-scroll-smooth body {
  overflow: hidden;
}

.has-scroll-smooth [data-scroll-container] {
  min-height: 100vh;
}

.c-scrollbar {
  position: absolute;
  right: 0;
  top: 0;
  width: 11px;
  height: 100vh;
  transform-origin: center right;
  transition: transform 0.3s, opacity 0.3s;
  opacity: 0;
}
.c-scrollbar:hover {
  transform: scaleX(1.45);
}
.c-scrollbar:hover,
.has-scroll-scrolling .c-scrollbar,
.has-scroll-dragging .c-scrollbar {
  opacity: 1;
}

.c-scrollbar_thumb {
  position: absolute;
  top: 0;
  right: 0;
  background-color: black;
  opacity: 0.5;
  width: 7px;
  border-radius: 10px;
  margin: 2px;
  cursor: -webkit-grab;
  cursor: grab;
}
.has-scroll-dragging .c-scrollbar_thumb {
  cursor: -webkit-grabbing;
  cursor: grabbing;
}
